
var spawn  = require ('child_process').spawn;
var zogLog = require ('zogLog');

exports.spawn = function (bin, args,
                          callbackDone,
                          callbackStdout,
                          callbackStderr)
{
  var prog = spawn (bin, args);

  prog.stdout.on ('data', function (data)
  {
    data.toString ().replace (/\r/g, '').split ('\n').forEach (function (line)
    {
      if (line.trim ().length)
      {
        if (callbackStdout)
          callbackStdout (line);
        else
          zogLog.verb (line);
      }
    });
  });

  prog.stderr.on ('data', function (data)
  {
    data.toString ().replace (/\r/g, '').split ('\n').forEach (function (line)
    {
      if (line.trim ().length)
      {
        if (callbackStderr)
          callbackStderr (line);
        else
          zogLog.err (line);
      }
    });
  });

  prog.on ('error', function (data)
  {
    zogLog.err (data);

    if (callbackDone)
      callbackDone (false);
  });

  prog.on ('close', function (code)
  {
    if (callbackDone)
      callbackDone (true);
  });
}
